import { LocalStorageI } from "../model/msgI"

export const localStorageHandler = (msg: LocalStorageI) => {
    const store = localStorage.getItem(msg.id)
        ? localStorage.getItem(msg.id)
        : null;
    let arr: string[] = [];
        if(!store) return; 
            
        console.log('store exist')
        arr = JSON.parse(store);
        arr.push(msg.img);
        localStorage.setItem(msg.id, JSON.stringify(arr));
        
}
