import { Figure } from "./Figure";

export class Line extends Figure {

    constructor(canvas: HTMLCanvasElement | null, socket: WebSocket | null, id: string) {
        super(canvas, socket, id);
    }

    public draw(ctx: CanvasRenderingContext2D, src: string, x: number, y: number, w: number, h: number) {
        super.draw(ctx, src, x, y, w, h);
        if(!this.img) return;

        this.img.onload = () => {
            if(!this.canvas || !this.ctx || !this.savedCanvas || !this.img) return;
            if(!this.startX || !this.startY) return;
            this.ctx.clearRect(0, 0, 600, 400);
            this.ctx.drawImage(this.img, 0, 0, 600, 400);
            this.ctx.beginPath();
            this.ctx.moveTo(x, y);
            this.ctx.lineTo(w+ this.startX, h+ this.startY);
            this.ctx.stroke();
            
        }
    }

   
}
