import { Figure } from "./Figure";

export class Rect extends Figure {

    constructor(canvas: HTMLCanvasElement | null, socket: WebSocket | null, id: string) {
        super(canvas, socket, id);
    }

    public draw(ctx: CanvasRenderingContext2D, x: number, y: number, w: number, h: number, src: string) {
        super.draw(ctx, x, y, w, h, src);

        this.img = document.createElement('img');
        this.img.src = src; 

        console.log(this.img)
        this.img.onload = () => {
            if(!this.canvas || !this.ctx || !this.savedCanvas) return;
            
            ctx.clearRect(0, 0, 600, 400);
            ctx.drawImage(this.img, 0, 0, 600, 400);
            ctx.fillRect(x, y, w, h);
        }
    }
}
