import { Tool } from "./Tool";

export class Figure extends Tool {
    protected startX?: number | null; 
    protected startY?: number | null; 
    protected mouseDown: boolean;
    protected img?: HTMLImageElement;

    constructor(canvas: HTMLCanvasElement | null, socket: WebSocket | null, id: string) {
        super(canvas, socket, id);
        this.mouseDown = false; 
        this.listen()
    }

    protected pointerdownHandler(e: PointerEvent): void {
        if(!this.canvas || !this.ctx || !this.socket) return; 
        
        this.startX = e.pageX - this.canvas.offsetLeft;
        this.startY = e.pageY - this.canvas.offsetTop;

        this.savedCanvas = this.canvas.toDataURL();
        this.mouseDown = true;

        this.socket.send(JSON.stringify({
            id: this.id,
            method: 'finish'
        }))
    }

    protected pointerUpHandler(e: PointerEvent) {
        this.mouseDown = false;
    }

    protected pointerMoveHandler(e: PointerEvent) {
        if(!this.canvas || !this.ctx || !this.startX || !this.startY || !this.socket) return;
        if(this.mouseDown) {
            let width = e.pageX - this.canvas.offsetLeft - this.startX,
                height = e.pageY - this.canvas.offsetTop - this.startY;

            if(!this.canvas || !this.ctx || !this.savedCanvas) return;
            
            this.socket.send(JSON.stringify({
                id: this.id,
                method: "draw",
                figure: {
                    x: this.startX,
                    y: this.startY,
                    w: width,
                    h: height
                },
                src: this.savedCanvas,
            }));
            //this.draw(this.startX, this.startY, width, height);
    
        }
    }

    public draw(ctx: CanvasRenderingContext2D, x: number, y: number, w: number, h: number) {
        super.draw(ctx, x, y, w, h);
    }

    protected listen() {
        if(!this.canvas) return;

        this.canvas.onpointerdown = this.pointerdownHandler.bind(this);
        this.canvas.onpointermove = this.pointerMoveHandler.bind(this);
        this.canvas.onpointerup = this.pointerUpHandler.bind(this);
    }
}
