import {MessageI} from '../model/msgI';
import { Brush } from '../tools/Brush';
import { Eraser } from '../tools/Eraser';
import canvasState from '../store/canvasState';
import toolState from '../store/toolState';
import { Rect } from '../tools/Rect';

export const connetUserToSession = (socket: WebSocket, id: string): Function => {

    return (username: string) => {
        canvasState.setWebSocket(socket);
        canvasState.setId(id);
    
        canvasState.socket?.send(JSON.stringify({
          id: id,
          method: 'connection',
          username: username
      }));
    }
  }

export const sokcetMessageHandler = (msg: MessageI) => {
    const figure = msg.figure;
    const [canvas, ctx, socket, id] = [canvasState.canvas, canvasState.ctx, canvasState.socket, canvasState.id];
    const savePrevTool = toolState.tool;
    if(!id || !ctx || !savePrevTool) return;

    switch (figure.type) {
        case 'brush':
            if(!toolState.tool) return;
            if(toolState.tool instanceof Brush) {
                toolState.tool.draw(ctx, figure.x, figure.y);
                toolState.setTool(savePrevTool);
            } else {
                toolState.setTool(new Brush(canvas, socket, id));
                if(!toolState.tool || !(toolState.tool instanceof Brush) ) return;
                toolState.tool.draw(ctx, figure.x, figure.y);
                toolState.setTool(savePrevTool);
            }
            
            break;
            
        case 'eraser':
            if(toolState.tool instanceof Eraser) {
                toolState.tool.draw(ctx, figure.x, figure.y);
                toolState.setTool(savePrevTool);
            } else {
                toolState.setTool(new Eraser(canvas, socket, id));
                if(!toolState.tool || !(toolState.tool instanceof Eraser) ) return;
                toolState.tool.draw(ctx, figure.x, figure.y);
                toolState.setTool(savePrevTool);
            }
            break;

        case 'rect':
            if(!figure.w || !figure.h) return;

            if(toolState.tool instanceof Rect) {
                toolState.tool.savedCanvas = canvasState.canvas?.toDataURL();
                toolState.tool.staticDraw(ctx, figure.x, figure.y, figure.w, figure.h);
            } else {
                toolState.setTool(new Rect(canvas, socket, id));
                if(!toolState.tool || !(toolState.tool instanceof Rect) ) return;

                toolState.tool.savedCanvas = canvasState.canvas?.toDataURL();
                toolState.tool.staticDraw(ctx, figure.x, figure.y, figure.w, figure.h);
                toolState.setTool(savePrevTool);
            }
            break;

        default:
            break;
    }
}
