import { FunctionExpression } from "typescript";
import { Figure } from "./Figure";
import { FireFunction } from "@testing-library/react";

export class Rect extends Figure {

    constructor(canvas: HTMLCanvasElement | null, socket: WebSocket | null, id: string) {
        super(canvas, socket, id);
    }

    public draw(ctx: CanvasRenderingContext2D, x: number, y: number, w: number, h: number): Function {
        super.draw(ctx, x, y, w, h);

        return (img: HTMLImageElement): void => {
            if(!this.img) return;

            this.img.onload = () => {
                if(!this.canvas || !this.ctx || !this.savedCanvas || !this.img) return;
                console.log(w)
                ctx.clearRect(0, 0, 600, 400);
                ctx.drawImage(img, 0, 0, 600, 400);
                ctx.fillRect(x, y, w, h);
            }
        }
    }
}
