import {makeAutoObservable} from 'mobx';


class CanvasState {
    canvas: HTMLCanvasElement | null = null;
    ctx: CanvasRenderingContext2D | null = null;
    undoArr: Array<string> = [];
    redoArr: Array<string> = [];
    
    constructor() {
        makeAutoObservable(this);

    }

    public setCanvas(canvas: HTMLCanvasElement | null): void {
        if(!canvas) return;

        this.canvas = canvas;
        this.ctx = canvas?.getContext("2d");
    }

    public pushToUndo() {
        if(!this.canvas) return;

        let src = this.canvas.toDataURL();
        this.undoArr.push(src);
        console.log(this.undo);
    }

    public pushToRedo() {

    }

    public undo() {
        if(this.undoArr.length !== 0 ) {
            let src = this.undoArr.pop() !== undefined? this.undoArr.pop(): null;
            if(!src) return;

            let img = document.createElement('img');
            img.src = src;

            img.onload = () => {
                if(!this.ctx) return;

                this.ctx.drawImage(img, 0, 0);
            }

        }
    }

    public redo() {

    }

}

export default new CanvasState();
