import { ConnectionI } from '../model/msgI';
import canvasState from '../store/canvasState';

export const connectionHandler = (msg: ConnectionI) => {
    console.log(msg.username);
        const savedCanvas = localStorage.getItem(msg.id)
            ? localStorage.getItem(msg.id)
            : null;
        const ctx = canvasState.ctx
            ? canvasState.ctx
            : null;

        if(savedCanvas && ctx) {
            const imgs: string[] = JSON.parse(savedCanvas);
            const img = document.createElement('img');
            img.src = imgs[imgs.length - 1];

            setTimeout(() => {
                ctx.drawImage(img, 0, 0, 600, 400);
            }, 0);
        }
}
