import  Figure  from "./Figure";

export default class Line extends Figure {

    public draw(ctx: CanvasRenderingContext2D, x: number, y: number, w: number, h: number) {

        this.img.onload = () => {
            if(!this.savedCanvas) return;
            if(!this.startX || !this.startY) return;
            this.ctx.clearRect(0, 0, 600, 400);
            this.ctx.drawImage(this.img, 0, 0, 600, 400);
            this.ctx.beginPath();
            this.ctx.moveTo(x, y);
            this.ctx.lineTo(w+ this.startX, h+ this.startY);
            this.ctx.stroke();
            
        }
    }

   
}
