import { StoreI } from '../model/msgI';
import canvasState from '../store/canvasState';

export const storeHandler = (msg: StoreI) => {

    switch (msg.type) {
        case 'undo':
            undoHandler(msg.id);
            break;
    
        default:
            break;
    }
}


function undoHandler(id: string) {
    if(!canvasState.socket) return;
    const lockStore = localStorage.getItem(id);
    if(!lockStore) return;

    const redoArr: string[] = JSON.parse(lockStore).redo;
    if(redoArr.length !== 0) {
        let src = redoArr[redoArr.length - 1];
        if(!src) return;
        
        const img = document.createElement('img');
        img.src = src;
        img.onload = () => {
        if(!canvasState.ctx) return;
        canvasState.ctx.clearRect(0, 0, 600, 400);
        canvasState.ctx.drawImage(img, 0, 0, 600, 400);
    }
}
}
