export class Tool {
    canvas: HTMLCanvasElement | null;

    constructor(canvas: HTMLCanvasElement | null) {
        this.canvas = canvas;
    }

    delete() {
        if(!this.canvas) return;

        this.canvas.onpointerdown = null;
        this.canvas.onpointermove = null;
        this.canvas.onpointerup = null;
    }
}
