import { FC, useRef, useEffect } from "react";
import '../styles/canvas.scss';

import canvasState from "../store/canvasState";
import toolState from "../store/toolState";
import { Brush } from "../tools/Brush";
import { observer } from "mobx-react";

interface CanvasI {
    id: string | null;
    name: string | null;
}

const Canvas: FC<CanvasI> = observer(({id, name}) => {
    const canvasRef = useRef<null | HTMLCanvasElement>(null);

    useEffect(() => {
        if(canvasRef.current && id && canvasState.socket) {
            canvasState.setCanvas(canvasRef.current);
            toolState.setTool(new Brush(canvasRef.current, canvasState.socket, id));

           canvasState.socket.onmessage = (msg) => {
                const obj = JSON.parse(msg.data);
                if(!toolState.tool || !canvasState.ctx) return;
                switch (obj.method) {
                    case 'draw':
                        console.log(obj.figure.w)
                        toolState.tool.draw(canvasState.ctx, obj.figure.x, obj.figure.y, obj.figure.w, obj.figure.h);
                        break;
                    case 'finish':
                        canvasState.pushToUndo();
                        canvasState.ctx.beginPath();
                        break;
                
                    default:
                        break;
                }

           }
        }
    }, [name]);

    const saveCanvas = () => {
        //canvasState.pushToUndo();
    }

    return (
        <div className="canvas">
            <canvas 
                width={600} 
                height={400}
                ref={canvasRef}
                onPointerDown={saveCanvas}
            />
        </div>
    )
});

export default Canvas;
