export default class Tool {
    protected canvas: HTMLCanvasElement;
    protected ctx: CanvasRenderingContext2D;
    protected socket: WebSocket;
    protected toolName: string;
    protected id: string;
    protected img?: HTMLImageElement;
    public savedCanvas: string;

    constructor(canvas: HTMLCanvasElement, socket: WebSocket, ctx: CanvasRenderingContext2D, id: string) {
        this.canvas = canvas;
        this.socket = socket;
        this.toolName = 'tool';
        this.id = id;
        this.ctx = ctx;
        this.savedCanvas = this.saveCanvas();
        this.delete();
    }

    get getToolName(): string {
        return this.toolName;
    }

    set fillColor(color: string) {
        if(color.startsWith('#')) {
            this.ctx.fillStyle = color;
        }
    }

    set styleColor(color: string) {
        if(color.startsWith('#')) {
            this.ctx.strokeStyle = color;
        }
    }

    set lineWidth(width: number) {
        this.ctx.lineWidth = width;
    }

    protected pointerUpHandler(e: PointerEvent) {
        localStorage.setItem( this.id, this.savedCanvas );
    }

    protected saveCanvas(): string {
        return this.canvas.toDataURL();
    }

    public setColor(color: string) {
        if(color.startsWith('#')) {
            this.ctx.strokeStyle = color;
            this.ctx.fillStyle = color;
        }
    }

    private delete() {
        this.canvas.onpointerdown = null;
        this.canvas.onpointermove = null;
        this.canvas.onpointerup = null;
    }
}


export type typedTool = Tool;
