import { Tool } from "./Tool";

export class Brush extends Tool {
    private startX?: number | null; 
    private startY?: number | null; 
    private mouseDown: boolean;

    constructor(canvas: HTMLCanvasElement | null) {
        super(canvas);
        this.mouseDown = false;
        this.listen();
    }

    private pointerdownHandler(e: PointerEvent): void {
        if(!this.canvas || !this.ctx) return; 
        
        this.startX = e.pageX - this.canvas.offsetLeft;
        this.startY = e.pageY - this.canvas.offsetTop;

        this.ctx.beginPath();
        this.ctx.moveTo(this.startX, this.startY)

        this.mouseDown = true;
    }

    private pointerUpHandler(e: PointerEvent) {
        this.mouseDown = false;
    }

    private pointerMoveHandler(e: PointerEvent) {
        if(!this.canvas || !this.ctx) return;
        if(this.mouseDown) {
            let currentX = e.pageX - this.canvas.offsetLeft,
                currentY = e.pageY - this.canvas.offsetTop;
            
            this.draw(currentX, currentY);
        }
    }

    private draw(x: number, y: number) {
        if(!this.canvas || !this.ctx) return;
        this.ctx.lineTo(x, y);
        this.ctx.stroke();
    }

    private listen() {
        if(!this.canvas) return;

        this.canvas.onpointerdown = this.pointerdownHandler.bind(this);
        this.canvas.onpointermove = this.pointerMoveHandler.bind(this);
        this.canvas.onpointerup = this.pointerUpHandler.bind(this);
    }
}
