import { FC, ChangeEvent} from "react";
import '../styles/bars.scss';

import { Brush, Eraser, Rect, Circle, Line } from "../tools/ToolsRouter";

import canvasState from "../store/canvasState";
import toolState from "../store/toolState";
import { Tool } from "../tools/ToolsRouter";

interface ToolbarI {
    id: string;
}


const Toolbar: FC<ToolbarI> = ({id}) => {

    const changeColor = (e: ChangeEvent<HTMLInputElement>) => {
        toolState.setStyleColor(e.target.value);
        toolState.setFillColor(e.target.value);
    }

   const genericArrow = <T, >(selectedTool: T) => {
        if( !canvasState.canvas || !canvasState.socket || !canvasState.ctx) return;

        if(selectedTool instanceof Tool) {
            toolState.setTool( new Tool(canvasState.canvas,  canvasState.socket, canvasState.ctx, id) )
        }
   }
   

    return (
        <div className="tool bar">
            <button 
                className="brush" 
                onClick={() => genericArrow(Brush)}
            />
            <button 
                className="rect" 
                onClick={() => genericArrow(Eraser)}
                />
            <button 
                className="circle"
                onClick={() => genericArrow(Circle)}
                />
            <button 
                className="eraser" 
                onClick={() => genericArrow(Eraser)}
                />
            <button 
                className="line" 
                onClick={() => genericArrow(Line)}
                />
            <input 
                type="color" 
                onChange={changeColor}
            />
            <button 
                className="undo" 
                onClick={() => canvasState.undo()}
                />
            <button 
                className="redo"
                onClick={() => canvasState.redo()}
                />
            <button className="save" />
        </div>
    )
}

export default Toolbar;
