import canvasState from '../store/canvasState';
import toolState from '../store/toolState';

import { ConnectionI } from '../model/msgI';


import { drawHandler } from './drawHandler';
import { changeSettingHandler } from './settingHandler';
import { localStorageHandler } from './localStorageHandler';

const connectionHandler = (msg: ConnectionI) => {
    console.log(msg.username);
        const savedCanvas = localStorage.getItem(msg.id)
            ? localStorage.getItem(msg.id)
            : null;
        const ctx = canvasState.ctx
            ? canvasState.ctx
            : null;

        if(savedCanvas && ctx) {
            const img = document.createElement('img');
            img.src = savedCanvas;
            ctx.drawImage(img, 0, 0, 600, 400);
        }
}

export const setSocketListener = (socket: WebSocket) => {
    socket.onmessage = (msg) => {
        const parsedMsg = JSON.parse(msg.data);
        if(!toolState.tool || !canvasState.ctx) return;
        switch (parsedMsg.method) {

            case 'connection': 
                connectionHandler(parsedMsg);
                break;

            case 'draw':
                drawHandler(parsedMsg);
                break;

            case 'setting': 
                changeSettingHandler(parsedMsg);
                break;

            case 'localStorage':
                localStorageHandler(parsedMsg)
                break;
    
            case 'finish':
                canvasState.pushToUndo();
                canvasState.ctx.beginPath();
                break;
        
            default:
                break;
        }
    
    }
} 

export const handlerUserConnection = (socket: WebSocket, id: string): Function => {

    return (username: string) => {
        canvasState.setWebSocket(socket);
        canvasState.setId(id);
    
        canvasState.socket?.send(JSON.stringify({
          id: id,
          method: 'connection',
          username: username
      }));
    }
  }
