import {makeAutoObservable} from 'mobx';


class CanvasState {
    canvas: HTMLCanvasElement | null = null;
    socket: WebSocket | null = null
    username: string | null = null;
    ctx: CanvasRenderingContext2D | null = null;
    id: string | null = null;
    undoArr: Array<string> = [];
    redoArr: Array<string> = [];
    
    constructor() {
        makeAutoObservable(this);

    }

    public checkForExist(): boolean {
        if(!this.id || !this.ctx || !this.canvas || !this.socket) return false;
        return true;
    }

    public setUsername(username: string) {
        this.username = username;
    }

    public setWebSocket(socket: WebSocket) {
        this.socket = socket;
    }

    public setCanvas(canvas: HTMLCanvasElement): void {
        this.canvas = canvas;
        this.ctx = canvas.getContext("2d");
    }

    public setId(id: string) {
        this.id = id;
    }

    public pushToUndo() {
        if(!this.canvas) return;

        let src = this.canvas.toDataURL();
        this.undoArr.push(src);
    }

    public pushToRedo() {
        if(!this.canvas) return;

        let src = this.canvas.toDataURL();
        this.redoArr.push(src);
    }

    public undo() {
        if(!this.id || !this.socket) return;
        const lockStore = localStorage.getItem(this.id);
        if(!lockStore) return;

        const undoArr: string[] = JSON.parse(lockStore).undo;
        const redoArr: string[] = JSON.parse(lockStore).redo;
        if(undoArr.length !== 0) {
            let src = undoArr.pop();
            if(!src) return;
            redoArr.push(src);
            localStorage.setItem(this.id, JSON.stringify({
            undo: undoArr,
            redo: redoArr
        }));

        const img = document.createElement('img');
        img.src = src;
        img.onload = () => {
            if(!this.ctx) return;
            this.ctx.clearRect(0, 0, 600, 400);
            this.ctx.drawImage(img, 0, 0, 600, 400);
        }
        
        this.socket.send(JSON.stringify({
            id: this.id,
            method: 'store',
            type: 'undo'
        }))
        }
    }

    public redo() {
        if(this.redoArr.length !== 0 ) {
            let src = this.redoArr.pop();
            if(typeof(src) !== 'string') return;

            let img = this.createImg(src);

            this.pushToUndo()

            img.onload = () => {
                if(!this.ctx) return;
                this.ctx.clearRect(0, 0, 600, 400);
                this.ctx.drawImage(img, 0, 0, 600, 400);
            }

        }
    }

    private createImg(src: string): HTMLImageElement {
        let img = document.createElement('img');
        img.src = src;

        return img;
    }

}

export default new CanvasState();
