import {MessageI} from '../model/msgI';
import { Brush } from '../tools/Brush';
import canvasState from '../store/canvasState';

export const sokcetMessageHandler = (msg: MessageI) => {
    const figure = msg.figure;
    const [canvas, ctx, socket, id] = [canvasState.canvas, canvasState.ctx, canvasState.socket, canvasState.id];
    if(!id || !ctx) return;

    switch (figure.type) {
        case 'brush':
            if(Brush) {
                new Brush(canvas, socket, id).draw(ctx, figure.x, figure.y);
            }
            break;
    
        default:
            break;
    }
}
