import Figure from "./Figure";

export default class Circle extends Figure {

    constructor(canvas: HTMLCanvasElement | null, socket: WebSocket | null, id: string) {
        super(canvas, socket, id);
    }


    public draw(ctx: CanvasRenderingContext2D, x: number, y: number, w: number, h: number) {
        if(!this.img) return;

        this.img.onload = () => {
            if(!this.canvas || !this.ctx || !this.savedCanvas || !this.img) return;
            let radius = this.getRadius(w);
            this.ctx.clearRect(0, 0, 600, 400);
            this.ctx.drawImage(this.img, 0, 0, 600, 400);
            this.ctx.beginPath();
            this.ctx.arc(x, y, radius, 0, 2 * Math.PI);
            this.ctx.fill();
        }
    }

    private getRadius(w: number): number {
        return  w > 0? w: w < 0? w * - 1: w = 0;
    }
}
