import { Figure } from "./Figure";

export class Rect extends Figure {

    constructor(canvas: HTMLCanvasElement | null, socket: WebSocket | null, id: number) {
        super(canvas, socket);
    }

    draw(x: number, y: number, w: number, h: number) {
        super.draw(x, y, w, h);
        if(!this.img) return;

        this.img.onload = () => {
            if(!this.canvas || !this.ctx || !this.savedCanvas || !this.img) return;

            this.ctx.clearRect(0, 0, 600, 400);
            this.ctx.drawImage(this.img, 0, 0, 600, 400);
            this.ctx.fillRect(x, y, w, h);
        }
    }
}
