import {makeAutoObservable} from 'mobx';


class CanvasState {
    canvas: HTMLCanvasElement | null = null;
    undo: Array<string> = [];
    redo: Array<string> = [];
    
    constructor() {
        makeAutoObservable(this);
    }

    setCanvas(canvas: HTMLCanvasElement | null): void {
        this.canvas = canvas;
    }
}

export default new CanvasState();
