import { FC, ChangeEvent, useState, useEffect } from "react";
import '../styles/bars.scss';

import { Tool as ToolConstructor } from "../tools/Tool";
import { Brush } from "../tools/Brush";
import { Eraser } from "../tools/Eraser";
import { Rect } from "../tools/Rect"; 
import { Circle } from "../tools/Circle";
import { Line } from "../tools/Line";

import canvasState from "../store/canvasState";
import toolState from "../store/toolState";

const Toolbar: FC = () => {

    const changeColor = (e: ChangeEvent<HTMLInputElement>) => {
        toolState.setStyleColor(e.target.value);
        toolState.setFillColor(e.target.value);
    }

    const setTool = (Tool: ToolConstructor) => {
        if(!canvasState.canvas || !canvasState.socket) return;

        toolState.setTool(new Tool(canvasState.canvas, canvasState.socket));
    }

    return (
        <div className="tool bar">
            <button 
                className="brush" 
                onClick={() => toolState.setTool(new Brush(
                    canvasState.canvas,
                    canvasState.socket
                ))}
            />
            <button 
                className="rect" 
                onClick={() => toolState.setTool(new Rect(canvasState.canvas))}
                />
            <button 
                className="circle"
                onClick={() => toolState.setTool(new Circle(canvasState.canvas))}
                />
            <button 
                className="eraser" 
                onClick={() => toolState.setTool(new Eraser(canvasState.canvas))}
                />
            <button 
                className="line" 
                onClick={() => toolState.setTool(new Line(canvasState.canvas))}
                />
            <input 
                type="color" 
                onChange={changeColor}
            />
            <button 
                className="undo" 
                onClick={() => canvasState.undo()}
                />
            <button 
                className="redo"
                onClick={() => canvasState.redo()}
                />
            <button className="save" />
        </div>
    )
}

export default Toolbar;
