import canvasState from '../store/canvasState';
import toolState from '../store/toolState';

import { sokcetMessageHandler } from './drawHandler';

export const setSocketListener = (socket: WebSocket) => {
    socket.onmessage = (msg) => {
        const parsedMsg = JSON.parse(msg.data);
        if(!toolState.tool || !canvasState.ctx) return;

        switch (parsedMsg.method) {
            case 'draw':
                sokcetMessageHandler(parsedMsg);
                break;
    
            case 'finish':
                canvasState.pushToUndo();
                canvasState.ctx.beginPath();
                break;
        
            default:
                break;
        }
    
    }
} 

export const handlerUserConnection = (socket: WebSocket, id: string): Function => {

    return (username: string) => {
        canvasState.setWebSocket(socket);
        canvasState.setId(id);
    
        canvasState.socket?.send(JSON.stringify({
          id: id,
          method: 'connection',
          username: username
      }));
    }
  }
