import { FC, ChangeEvent} from "react";
import '../styles/bars.scss';

import { Brush, Eraser, Rect, Circle, Line } from "../tools/ToolsRouter";

import canvasState from "../store/canvasState";
import toolState from "../store/toolState";

interface ToolbarI {
    id: string;
}

const Toolbar: FC<ToolbarI> = ({id}) => {

    const changeColor = (e: ChangeEvent<HTMLInputElement>) => {
        toolState.setStyleColor(e.target.value);
        toolState.setFillColor(e.target.value);
    }
    if(canvasState.canvas instanceof HTMLCanvasElement 
        || canvasState.socket instanceof WebSocket 
        || canvasState.ctx instanceof CanvasRenderingContext2D) {
        return (
            <div className="tool bar">
                <button 
                    className="brush" 
                    onClick={() => toolState.setTool(new Brush(
                        canvasState.canvas,
                        canvasState.socket,
                        canvasState.ctx,
                        id
                    ))}
                />
                <button 
                    className="rect" 
                    onClick={() => toolState.setTool(new Rect(
                        canvasState.canvas,
                        canvasState.socket,
                        canvasState.ctx,
                        id
                        ))}
                    />
                <button 
                    className="circle"
                    onClick={() => toolState.setTool(new Circle(
                        canvasState.canvas,
                        canvasState.socket,
                        canvasState.ctx,
                        id
                        ))}
                    />
                <button 
                    className="eraser" 
                    onClick={() => toolState.setTool(new Eraser(
                        canvasState.canvas,
                        canvasState.socket,
                        canvasState.ctx,
                        id
                        ))}
                    />
                <button 
                    className="line" 
                    onClick={() => toolState.setTool(new Line(
                        canvasState.canvas,
                        canvasState.socket,
                        canvasState.ctx,
                        id
                        ))}
                    />
                <input 
                    type="color" 
                    onChange={changeColor}
                />
                <button 
                    className="undo" 
                    onClick={() => canvasState.undo()}
                    />
                <button 
                    className="redo"
                    onClick={() => canvasState.redo()}
                    />
                <button className="save" />
            </div>
        )
    }
    
}

export default Toolbar;
