import {useEffect} from 'react';
import {useParams} from 'react-router-dom';


import Toolbar from './components/Toolbar';
import Settingbar from './components/Settingbar';
import Canvas from './components/Canvas';
import Modal from './components/Modal';

import canvasState from './store/canvasState';

import { handlerUserConnection } from './sokcet/socket';

import './styles/app.scss';

const socket = new WebSocket('ws://localhost:4000');

function App() {

  useEffect(() => {
    const [ctx, id] = [
      canvasState.ctx, 
      canvasState.id
  ];


    if(!ctx || !id) return;
    const savedCanvas = localStorage.getItem(id)
        ? localStorage.getItem(id)
        : null;
    console.log(savedCanvas);
    if(savedCanvas) {
        const img = document.createElement('img');
        img.src = savedCanvas;
        ctx.drawImage(img, 0, 0, 600, 400);
    }
  }, [id]);

  const {id} = useParams();
  if(id) {
    const connectionHandler = handlerUserConnection(socket, id);


    return (
      <div className="App">
        <Modal connectionHandler={connectionHandler}/>
        <Toolbar id={id}/>
        <Settingbar />
        <Canvas />
      </div>
    ) 
  } else return (
    <></>
  )


}

export default App;
