import {makeAutoObservable} from 'mobx';
import { Tool } from '../tools/Tool';

class ToolState {
    tool: Tool | null = null;
    color: string = '#000';

    constructor() {
        makeAutoObservable(this);
    }

    setTool(tool : Tool): void {
        this.tool = tool;
    }
}

export default new ToolState();
