import {MessageDrawI} from '../model/msgI';
import { Brush, Circle, Eraser, Rect, Line } from '../tools/ToolsRouter';
import canvasState from '../store/canvasState';
import toolState from '../store/toolState';


export const sokcetMessageHandler = (msg: MessageDrawI) => {
    if(!toolState.tool) return;

    const figure = msg.figure;
    const [canvas, ctx, socket, id] = [
        canvasState.canvas, 
        canvasState.ctx, 
        canvasState.socket, 
        canvasState.id
    ];
    const [tool, toolName] = [
        toolState.tool,
        toolState.tool.getToolName
    ]

    if(!id || !ctx || !canvas || !socket) return;

    switch (figure.type) {
        case 'brush':
            if(toolName === 'brush' && tool instanceof Brush) {
                tool.draw(ctx, figure.x, figure.y);
            } else {
                toolState.setTool(new Brush(canvas, socket, ctx, id));
                if(!toolState.tool || !(toolState.tool instanceof Brush) ) return;
                toolState.tool.draw(ctx, figure.x, figure.y);
              
            }
            break;
            
        case 'eraser':
            if(toolName === 'eraser' && tool instanceof Brush) {
                tool.draw(ctx, figure.x, figure.y);
               
            } else {
                toolState.setTool(new Eraser(canvas, socket, ctx, id));
                if(!toolState.tool || !(toolState.tool instanceof Eraser) ) return;
                toolState.tool.draw(ctx, figure.x, figure.y);
            }
            break;

        case 'rect':
            if(!figure.w || !figure.h) return;

            if(toolName === 'rect' && tool instanceof Rect) {
                tool.savedCanvas = canvas.toDataURL();
                tool.staticDraw(ctx, figure.x, figure.y, figure.w, figure.h);
            } else {
                toolState.setTool(new Rect(canvas, socket, ctx, id));
                if(!toolState.tool || !(toolState.tool instanceof Rect) ) return;

                toolState.tool.savedCanvas = canvas.toDataURL();
                toolState.tool.staticDraw(ctx, figure.x, figure.y, figure.w, figure.h);
            }
            break;

        case 'circle':
            if(!figure.w || !figure.h) return;

            if(toolName === 'circle' && tool instanceof Circle) {
                tool.savedCanvas = canvas.toDataURL();
                tool.staticDraw(ctx, figure.x, figure.y, figure.w, figure.h);
            } else {
                toolState.setTool(new Circle(canvas, socket, ctx, id));
                if(!toolState.tool || !(toolState.tool instanceof Circle) ) return;

                toolState.tool.savedCanvas = canvas.toDataURL();
                toolState.tool.staticDraw(ctx, figure.x, figure.y, figure.w, figure.h);
            }
            break;

        case 'line':
            if(!figure.w || !figure.h) return;

            if(toolName === 'line' && tool instanceof Line) {
                tool.savedCanvas = canvas.toDataURL();
                tool.staticDraw(ctx, figure.x, figure.y, figure.w, figure.h);
            } else {
                toolState.setTool(new Line(canvas, socket, ctx, id));
                if(!toolState.tool || !(toolState.tool instanceof Line) ) return;
            
                toolState.tool.savedCanvas = canvas.toDataURL();
                toolState.tool.staticDraw(ctx, figure.x, figure.y, figure.w, figure.h);
            }
            break;

        default:
            break;
    }
}
