import { FC, useRef, useEffect } from "react";
import { observer } from "mobx-react";
import '../styles/canvas.scss';

import canvasState from "../store/canvasState";
import toolState from "../store/toolState";

import { Brush } from "../tools/ToolsRouter";

import {setSocketListener} from '../sokcet/socket';

interface CanvasI {
    id: string | null;
}

const Canvas: FC<CanvasI> = observer(({id }) => {
    const canvasRef = useRef<null | HTMLCanvasElement>(null);

    useEffect(() => {
        if(canvasRef.current && id && canvasState.socket) {
            canvasState.setCanvas(canvasRef.current);
            toolState.setTool(new Brush(canvasRef.current, canvasState.socket, id, canvasRef.current.ctx));
            setSocketListener(canvasState.socket);
        }
    }, [canvasState.username]);

    return (
        <div className="canvas">
            <canvas 
                width={600} 
                height={400}
                ref={canvasRef}
            />
        </div>
    )
});

export default Canvas;
