import {makeAutoObservable} from 'mobx';


export default class CanvasState {
    canvas: HTMLCanvasElement | null = null;
    ctx: CanvasRenderingContext2D | null = null;
    username: string | null = null;
    undoArr: Array<string> = [];
    redoArr: Array<string> = [];
    socket: WebSocket | null = null
    id: string | null = null;
    
    constructor(canvas: HTMLCanvasElement, socket: WebSocket, id: string) {
        makeAutoObservable(this);

    }

    public setUsername(username: string) {
        this.username = username;
    }

    public setWebSocket(socket: WebSocket) {
        this.socket = socket;
    }

    public setCanvas(canvas: HTMLCanvasElement): void {
        this.canvas = canvas;
        this.ctx = canvas.getContext("2d");
    }

    public setId(id: string) {
        this.id = id;
    }

    public pushToUndo() {
        if(!this.canvas) return;

        let src = this.canvas.toDataURL();
        this.undoArr.push(src);
    }

    public pushToRedo() {
        if(!this.canvas) return;

        let src = this.canvas.toDataURL();
        this.redoArr.push(src);
    }

    public undo() {
        if(this.undoArr.length !== 0 ) {
            let src = this.undoArr.pop();
            if(typeof(src) !== 'string') return;

            let img = document.createElement('img');
            img.src = src;

            this.pushToRedo()

            img.onload = () => {
                if(!this.ctx) return;
                this.ctx.clearRect(0, 0, 600, 400);
                this.ctx.drawImage(img, 0, 0, 600, 400);
            }

        }
    }

    public redo() {
        if(this.redoArr.length !== 0 ) {
            let src = this.redoArr.pop();
            if(typeof(src) !== 'string') return;

            let img = this.createImg(src);

            this.pushToUndo()

            img.onload = () => {
                if(!this.ctx) return;
                this.ctx.clearRect(0, 0, 600, 400);
                this.ctx.drawImage(img, 0, 0, 600, 400);
            }

        }
    }

    private createImg(src: string): HTMLImageElement {
        let img = document.createElement('img');
        img.src = src;

        return img;
    }

}
