import { Brush } from "./Brush";

export class Eraser extends Brush {
    private saved: string | CanvasGradient | CanvasPattern | null;
    private color: string;

    constructor(canvas : HTMLCanvasElement | null, socket: WebSocket | null, id: string) {
        super(canvas, socket, id);
        this.saved = this.ctx? this.ctx.strokeStyle: null;
        this.color = '#fff';
    }

    protected pointerMoveHandler(e: PointerEvent) {
        
    }

    public draw(ctx: CanvasRenderingContext2D, x: number, y: number) {
        if(!this.ctx || !this.canvas) return;
        this.ctx.strokeStyle = this.color;
        super.draw(ctx, x, y);
        if(this.saved) {
            this.ctx.strokeStyle = this.saved
        }
    }
}
