import { Figure } from "./Figure";

export class Rect extends Figure {

    constructor(canvas: HTMLCanvasElement | null, socket: WebSocket | null, id: string) {
        super(canvas, socket, id);
    }

    protected pointerMoveHandler(e: PointerEvent) {
        super.pointerMoveHandler(e);
        if(!this.ctx || !this.startX || !this.startY || !this.width || !this.height) return;
        this.draw(this.ctx, this.startX, this.startY, this.width, this.height);
    }

    public draw(ctx: CanvasRenderingContext2D, x: number, y: number, w: number, h: number) {
        super.draw(ctx, x, y, w, h);
        if(!this.img) return false;

        ctx.clearRect(0, 0, 600, 400);
        ctx.beginPath();
        ctx.rect(x, y, w, h);
        ctx.fill();
        ctx.stroke();
    
    }
}
